import React from "react";
import { useNavigate } from "react-router-dom";
import "./BottomNav.css";
import { FaHome, FaTicketAlt, FaHeart, FaMapMarkedAlt, FaCube } from "react-icons/fa";

// Définition du composant NavItem avec gestion du clic
const NavItem = ({ icon, active = false, onClick }) => {
  return (
    <div 
      className={`nav-item ${active ? "active" : ""}`}
      onClick={onClick}
    >
      {icon}
    </div>
  );
};

const BottomNav = () => {
  const navigate = useNavigate();

  return (
    <div className="bottom-nav">
      <NavItem 
        icon={<FaHome />} 
        active 
        onClick={() => navigate('/home')} 
      />
      <NavItem icon={<FaTicketAlt />} />
      <NavItem icon={<FaHeart />} />
      <NavItem icon={<FaMapMarkedAlt />} />
      <NavItem icon={<FaCube />} />
    </div>
  );
};

export default BottomNav;
