import { FaBell, FaBars } from 'react-icons/fa';

export default function MobileNavbar() {
  return (
    <nav className="bg-white px-4 py-2 flex justify-between items-center shadow-sm md:hidden">
      
      {/* Menu (3 lignes Ã  gauche) */}
      <FaBars className="text-xl text-black" />

      {/* Droite : cloche + profil */}
      <div className="flex items-center gap-2">
        
        {/* Cloche de notification */}
        <div className="w-8 h-8 bg-white rounded-full shadow flex items-center justify-center relative">
          <FaBell className="text-sm text-black" />
          <span className="absolute top-0.5 right-0.5 w-1.5 h-1.5 bg-cyan-500 rounded-full"></span>
        </div>

        {/* Avatar */}
        <div className="w-8 h-8 bg-white rounded-full shadow flex items-center justify-center">
          <img
            src="https://randomuser.me/api/portraits/women/44.jpg" // Remplacez par une URL valide
            alt="Profil"
            className="w-6 h-6 rounded-full object-cover"
            onError={(e) => {
              e.target.onerror = null; 
              e.target.src = "data:image/svg+xml;base64,"
            }}
          />
        </div>

      </div>
    </nav>
  );
}
