import React from 'react';
import { useLocation } from 'react-router-dom';
import './TicketPage.css';
import Navbar from './navbar';

const TicketPage = () => {
  const location = useLocation();
  const { ride } = location.state || {};

  if (!ride) {
    return <div>No ride selected</div>;
  }

  return (
        <div className="booking-page">
      <Navbar/> {/* La navbar que nous avons créée */}
      
      <div className="booking-container">
        <a href="/" className="back-link">← Back to homepage</a>
{/*         
        <h1 className="booking-title">Book Your Trip</h1> */}
        
        <div className="booking-steps">
          <div className="step active">
            <div className="step-number">1</div>
            <div className="step-title">Select your ride</div>
          </div>
          
          <div className="step">
            <div className="step-number">2</div>
            <div className="step-title">Select your ticket</div>
          </div>
          
          <div className="step">
            <div className="step-number">3</div>
            <div className="step-title">Pay</div>
          </div>
        </div>
    
        
        {/* Contenu spécifique à l'étape 1 */}
        <div className="step-content"></div>    </div>
        </div>
    /* <div className="ticket-container">
      <h2>Your Ticket</h2>
      
      <div className="ticket-card">
        <div className="ticket-header">
          <img src={ride.icon} alt={ride.name} className="ticket-icon" />
          <h3>{ride.name}</h3>
        </div>
        
        <div className="ticket-details">
          <div className="detail">
            <span>Departure:</span>
            <strong>{ride.time}</strong>
          </div>
          
          <div className="detail">
            <span>Price:</span>
            <strong>{ride.price}</strong>
          </div>
          
          {ride.eta && (
            <div className="detail">
              <span>ETA:</span>
              <strong>{ride.eta}</strong>
            </div>
          )}
        </div>
        
        <div className="ticket-qr">
          {/* Placeholder pour QR code */
          /* <div className="qr-placeholder"></div>
          <p>Scan this code for boarding</p>
        </div>
      </div>
      
      <button className="print-button">Print Ticket</button>
    </div> */
  ); 
};

export default TicketPage;
