import React from 'react';
import { ScrollView, View, Text, Image, StyleSheet } from 'react-native';

const Facilities = () => {
  const facilities = [
    { 
      name: 'Outdoor Pool', 
      icon: 'https://cdn-icons-png.flaticon.com/512/2853/2853306.png' 
    },
    { 
      name: '1 Bathtub', 
      icon: 'https://cdn-icons-png.flaticon.com/512/190/190708.png' 
    },
    { 
      name: 'Free Wi-Fi', 
      icon: 'https://cdn-icons-png.flaticon.com/512/2936/2936919.png' 
    },
    { 
      name: 'Free Breakfast', 
      icon: 'https://cdn-icons-png.flaticon.com/512/3081/3081985.png' 
    },
    { 
      name: 'Bee Accessible', 
      icon: 'https://cdn-icons-png.flaticon.com/512/284/284443.png' 
    }
  ];

  return (
    <View style={styles.container}>
      <Text style={styles.title}>FACILITIES</Text>
      <ScrollView horizontal showsHorizontalScrollIndicator={false}>
        {facilities.map((facility, index) => (
          <View key={index} style={styles.card}>
            <Image 
              source={{ uri: facility.icon }} 
              style={styles.icon} 
              resizeMode="contain"
            />
            <Text style={styles.name}>{facility.name}</Text>
          </View>
        ))}
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    marginVertical: 15,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
    paddingHorizontal: 15,
  },
  card: {
    width: 100,
    marginRight: 15,
    alignItems: 'center',
    backgroundColor: '#f8f8f8',
    borderRadius: 10,
    padding: 10,
    marginLeft: index => index === 0 ? 15 : 0,
  },
  icon: {
    width: 40,
    height: 40,
    marginBottom: 5,
  },
  name: {
    fontSize: 12,
    textAlign: 'center',
    flexWrap: 'wrap',
  },
});

export default Facilities;
