import React from 'react';
import { useLocation } from 'react-router-dom';
import { FaClock, FaMapMarkerAlt, FaInfoCircle, FaShoppingCart, FaCar, FaWalking } from 'react-icons/fa';
import './ExperienceDetails.css';

const ExperienceDetails = () => {
  const location = useLocation();
  const { experience } = location.state || {};

  if (!experience) {
    return <div className="not-found">No experience data found</div>;
  }

  return (
    <div className="experience-detail-container">
      <div className="detail-header">
        <div className="detail-image" style={{ backgroundImage: `url(${experience.image})` }}>
          <h1 className="detail-title">{experience.title}</h1>
        </div>
      </div>

      <div className="detail-body">
        <div className="detail-highlight">
          <p>
           {experience.history}
            {fullHistory.length > shortHistory.length && (
              <button 
                onClick={() => setShowFullHistory(!showFullHistory)} 
                className="read-more-btn"
              >
                {showFullHistory ? (
                  <>
                    <FaChevronUp /> Show less
                  </>
                ) : (
                  <>
                    <FaChevronDown /> Learn more
                  </>
                )}
              </button>
            )}
          </p>
        </div>

        <div className="detail-info">
          <div className="price-info">
            <span className="price">{experience.price}</span>
            <div className="info-row">
              <FaInfoCircle className="icon" />
              <span>{experience.guidedTour}</span>
            </div>
            <div className="info-row">
              <FaMapMarkerAlt className="icon" />
              <span>{experience.distance}</span>
            </div>
          </div>

          <div className="time-info">
            <div className="time">
              <FaCar className="icon" />
              <span>{experience.times[0]}</span>
            </div>
            <div className="time">
              <FaWalking className="icon" />
              <span>{experience.times[1]}</span>
            </div>
          </div>
        </div>

        <div className="detail-description">
          <h2>About</h2>
          <p>{experience.description || "This magnificent mosque is one of the few religious sites open to non-Muslims. The minaret is the world's tallest at 210 meters."}</p>
        </div>

      
      </div>
    </div>
  );
};

export default ExperienceDetails;
