import React from 'react';
import './ExperienceCards.css';
import { FaClock, FaMapMarkerAlt, FaInfoCircle, FaShoppingCart } from 'react-icons/fa';
import { FaCar } from "react-icons/fa";
import { FaWalking } from "react-icons/fa";
import { useNavigate, } from 'react-router-dom';
// Dans ton JSX :
<FaCar className="icon" />

const ExperienceCards = () => {
  
  const navigate = useNavigate();
  const experiences = [
    {
      id: 1,
    title: "Ancienne Médina",
    price: "Free",
    guidedTour: "Guided tour from MAD 400",
    distance: "Distance from Hassan II Mosque",
    times: ["4 min", "20 min"],
    image: "https://tse4.explicit.bing.net/th?id=OIP.v50Vitgvj6Esegh4_OpM9gHaEo&pid=Api&P=0&h=180",
    history: "On August 30, 1999, the Hassan II Mosque in Casablanca, Morocco, was officially inaugurated. This grand event marked the completion of one of the most magnificent religious and architectural landmarks in the Islamic world. Commissioned by King Hassan II, the mosque was designed by French architect Michel Pinseau and constructed by the Moroccan company Bouygues. The construction began in 1986 and took over seven years, employing thousands of Moroccan artisans who contributed their skills in wood carving, zellij (tile work), and marble.",
    mustKnow: [
      "Wear comfortable shoes",
      "Bargaining is expected in souks",
      "Respect prayer areas",
      "Best visited in early morning or late afternoon"
    ],
    highlights: [
      "Traditional Moroccan architecture",
      "Close to Hassan II Mosque",
      "Authentic local crafts"
    ], openingTimes: {
    days: "Lundi à Dimanche",
    hours: "8h00 - 18h00",
    closed: "Fermé les jours fériés"
  }},
  ];

  const handleLearnMore = (experience) => {
    navigate(`/experience/${experience.id}`, { state: { experience } });
  };
  return (
    <div className="experiences-container">
      {experiences.map((exp, index) => (
        <div key={index} className="experience-card">
          <div className="card-image-container">
            <div 
              className="card-image"
              style={{ 
                backgroundImage: `url(${exp.image})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center'
              }}
            >
              <h3 className="card-title">{exp.title}</h3>
            </div>
          </div>
          
          <div className="card-content">
            <div className="price-info">
              <span className="price">{exp.price}</span>
              <div className="info-row">
                <FaInfoCircle className="icon" />
                <span>{exp.guidedTour}</span>
              </div>
              <div className="info-row">
                <FaMapMarkerAlt className="icon" />
                <span>{exp.distance}</span>
              </div>
            </div>
            
            <div className="time-info">
              <div className="time">
                <FaCar className="icon" />
                <span>{exp.times[0]}</span>
              </div>
              <div className="time">
                <FaWalking  className="icon" />
                <span>{exp.times[1]}</span>
              </div>
            </div>
            
            <div className="card-actions">
              <button className="action-btn learn-more"
               onClick={() => handleLearnMore(exp)}>
                <FaInfoCircle className="action-icon" />
                Learn more
              </button>
              <button className="action-btn buy">
                <FaShoppingCart className="action-icon" />
                Buy
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ExperienceCards;
