import React from 'react';
import { useLocation } from 'react-router-dom';
import './ExperienceDetails.css';

const ExperienceDetails = () => {
  const location = useLocation();
  const { experience } = location.state || {};

  if (!experience) {
    return <div>No experience data found</div>;
  }

  return (
    <div className="experience-details">
      <h1>{experience.title}</h1>
      <img src={experience.image} alt={experience.title} />
      
      <div className="detail-content">
        <div className="highlight-box">
          <p>On August 30, 1999, the Hassan II Mosque in Casablanca was officially inaugurated, marking the completion of this architectural marvel.</p>
        </div>
        
        <div className="detail-section">
          <h2>About</h2>
          <p>{experience.description || "This magnificent mosque is one of the few religious sites open to non-Muslims. The minaret is the world's tallest at 210 meters."}</p>
        </div>
        
        <div className="info-grid">
          <div className="info-item">
            <h3>Price</h3>
            <p>{experience.price}</p>
          </div>
          
          <div className="info-item">
            <h3>Guided Tours</h3>
            <p>{experience.guidedTour}</p>
          </div>
          
          <div className="info-item">
            <h3>Distance</h3>
            <p>{experience.distance}</p>
          </div>
          
          <div className="info-item">
            <h3>Transport Times</h3>
            <p>By car: {experience.times[0]}</p>
            <p>Walking: {experience.times[1]}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ExperienceDetails;
