import { FaBell, FaBars } from 'react-icons/fa';

export default function MobileNavbar() {
  return (
    <nav className="bg-white px-4 py-3 flex justify-between items-center shadow-sm md:hidden">
      
      {/* G A U C H E  :  Menu (liste) */}
      <div className="flex items-center">
        <FaBars className="text-2xl text-black" />
      </div>

      {/* D R O I T E  :  Notification + Image de profil */}
      <div className="flex items-center gap-3">
        {/* Notification dans un cercle blanc */}
        <div className="w-10 h-10 flex items-center justify-center bg-white rounded-full shadow-md relative">
          <FaBell className="text-xl text-black" />
          <span className="absolute top-1 right-1 w-2 h-2 bg-cyan-500 rounded-full"></span>
        </div>

{/* Image de profil dans un petit cercle blanc */}
<div className="w-8 h-8 rounded-full bg-white shadow-md flex items-center justify-center">
  <img
    src="https://a.storyblok.com/f/191576/1200x800/215e59568f/round_profil_picture_after_.webp"
    alt="Profil"
    className="w-6 h-6 rounded-full object-cover"
  />
</div>

      </div>
    </nav>
  );
}
