import React, { useState } from 'react';
import './RideOptions.css';
import { useNavigate } from 'react-router-dom';
const RideOptions = () => {
  const [selectedRide, setSelectedRide] = useState(null);
 const navigate = useNavigate();
   const handleConfirm = () => {
    if (selectedRide) {
      navigate('/ticket', { state: { ride: selectedRide } });
    }
  };
  const rides = [
    {
      name: "UberX",
      time: "8:31am",
      price: "510DH",
      type: "standard",
      eta: "",
      icon: "/image.png"
    },
    {
      name: "Comfort Electric",
      time: "8:36am",
      price: "550DH",
      type: "electric",
      eta: "5 min away",
      icon: "/comfort_header.jpg"
    },
    {
      name: "Black",
      time: "8:38am",
      price: "570DH",
      type: "premium",
      eta: "",
      icon: "https://tse3.mm.bing.net/th?id=OIP._acU7XIIbdHkNPlQtE3FZwAAAA&pid=Api&P=0&h=180"
    }
  ];

  return (
    <div className="ride-options-container">
      <div className="rides-list">
        {rides.map((ride, index) => (
          <div 
            key={index} 
            className={`ride-card ${ride.type} ${selectedRide === index ? 'selected' : ''}`}
            onClick={() => setSelectedRide(selectedRide === index ? null : index)}
          >
            <div className="ride-icon">
              <img src={ride.icon} alt={ride.name} className="ride-image-icon" />
            </div>
            
            <div className="ride-info">
              <div className="ride-name-time">
                <span className="ride-name">{ride.name}</span>
                <span className="ride-time">
                  {ride.time}
                  {ride.eta && <span className="ride-eta"> - {ride.eta}</span>}
                </span>
              </div>
            </div>
            
            <div className="ride-price">{ride.price}</div>
          </div>
        ))}
      </div>
        <button 
        className={`confirm-button ${!selectedRide ? 'disabled' : ''}`}
        onClick={handleConfirm}
        disabled={!selectedRide}
      >
        Confirm and View Ticket
      </button>
    </div>
  );
};

export default RideOptions;
