import { Menu, Bell } from 'lucide-react';

const Navbar = () => {
  return (
    <nav className="w-full bg-white shadow-md px-4 py-2 flex items-center justify-between">
      {/* Icône Liste (Menu) à gauche */}
      <button className="text-gray-700 hover:text-black">
        <Menu size={24} />
      </button>

      <h1 className="text-lg font-semibold text-gray-800">Mon Application</h1>

      {/* Icône Notification à droite */}
      <button className="text-gray-700 hover:text-black relative">
        <Bell size={24} />
        {/* Badge de notification */}
        <span className="absolute top-0 right-0 inline-block w-2 h-2 bg-red-500 rounded-full animate-ping"></span>
      </button>
    </nav>
  );
};

export default Navbar;
