import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './RideOptions.css';

const RideOptions = () => {
  const [selectedRide, setSelectedRide] = useState(null);
  const navigate = useNavigate();

  const rides = [
    {
      id: 1,
      name: "UberX",
      time: "8:31am",
      price: "$10.00",
      type: "standard",
      eta: "",
      icon: "/image.png"
    },
    {
      id: 2,
      name: "Comfort Electric",
      time: "8:36am",
      price: "$15.00",
      type: "electric",
      eta: "5 min away",
      icon: "/comfort_header.jpg"
    },
    {
      id: 3,
      name: "Black",
      time: "8:38am",
      price: "$30.00",
      type: "premium",
      eta: "",
      icon: "https://tse3.mm.bing.net/th?id=OIP._acU7XIIbdHkNPlQtE3FZwAAAA&pid=Api&P=0&h=180"
    }
  ];

  const handleSelect = (ride) => {
    setSelectedRide(ride);
    navigate('/confirmation', { state: { selectedRide: ride } });
  };

  return (
    <div className="ride-options-container">
      <div className="rides-list">
        {rides.map((ride, index) => (
          <div 
            key={ride.id}
            className={`ride-card ${ride.type} ${selectedRide?.id === ride.id ? 'selected' : ''}`}
          >
            <div className="ride-icon">
              <img src={ride.icon} alt={ride.name} className="ride-image-icon" />
            </div>
            
            <div className="ride-info">
              <div className="ride-name-time">
                <span className="ride-name">{ride.name}</span>
                <span className="ride-time">
                  {ride.time}
                  {ride.eta && <span className="ride-eta"> - {ride.eta}</span>}
                </span>
              </div>
            </div>
            
            <div className="ride-price">{ride.price}</div>

            <button 
              className={`select-button ${selectedRide?.id === ride.id ? 'selected' : ''}`}
              onClick={() => handleSelect(ride)}
            >
              {selectedRide?.id === ride.id ? 'Selected' : 'Select this option'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RideOptions;
