import React, { useState } from 'react';
import './Navbar.css';
// Pour les icÃ´nes (optionnel - installez avec: npm install react-icons)
import { FaList, FaBell } from 'react-icons/fa';
import profileImage from './profile.jpg'; // Importez votre image de profil

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [notifCount, setNotifCount] = useState(3); // Exemple de compteur de notifications

  return (
    <nav className="navbar">
      <div className="navbar-container">
        {/* Logo */}
        <div className="navbar-logo">MonApp</div>

        {/* Menu mobile */}
        <div className="menu-icon" onClick={() => setIsMenuOpen(!isMenuOpen)}>
          {isMenuOpen ? 'âœ•' : 'â˜°'}
        </div>

        {/* Items de navigation */}
        <ul className={`nav-items ${isMenuOpen ? 'active' : ''}`}>
          <li className="nav-item">
            <a href="#list" className="nav-link">
              <FaList className="nav-icon" /> {/* Ou utilisez ðŸ“‹ */}
              <span>List</span>
            </a>
          </li>
          
          <li className="nav-item profile-item">
            <a href="#profile" className="nav-link">
              <img 
                src="https://a.storyblok.com/f/191576/1200x800/215e59568f/round_profil_picture_after_.webp"
                alt="Profile" 
                className="profile-image"
              />
              <span>Profile</span>
            </a>
          </li>
          
          <li className="nav-item notif-item">
            <a href="#notif" className="nav-link">
              <FaBell className="nav-icon" /> {/* Ou utilisez ðŸ”” */}
              {notifCount > 0 && (
                <span className="notif-badge">{notifCount}</span>
              )}
              <span>Notif</span>
            </a>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
