import { useState } from "react";
import { FaBell, FaUserCircle, FaBars } from "react-icons/fa";

export default function MobileNavbar() {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <>
      <nav className="bg-white shadow-md p-4 flex justify-between items-center md:hidden">
        <button onClick={() => setMenuOpen(true)}>
          <FaBars className="" />
        </button>
        <h1 className="text-xl font-bold">MonApp</h1>
        <div className="flex gap-4 items-center">
          <FaBell className="text-2xl" />
          <FaUserCircle className="text-2xl" />
        </div>
      </nav>

      {/* Overlay quand menu est ouvert */}
      {menuOpen && (
        <>
          {/* Fond flouté / assombri */}
          <div
            className="fixed inset-0 bg-black bg-opacity-50 z-10"
            onClick={() => setMenuOpen(false)}
          ></div>

          {/* Menu latéral blanc */}
          <div className="fixed top-0 left-0 w-64 h-full bg-white shadow-lg z-20 p-4 transition-transform duration-300">
            <button
              onClick={() => setMenuOpen(false)}
              className="mb-4 font-semibold text-gray-600"
            >
              Fermer ✖
            </button>
            <ul className="space-y-4">
              <li><a href="#" className="text-gray-800">Accueil</a></li>
              <li><a href="#" className="text-gray-800">Profil</a></li>
              <li><a href="#" className="text-gray-800">Paramètres</a></li>
              <li><a href="#" className="text-gray-800">Déconnexion</a></li>
            </ul>
          </div>
        </>
      )}
    </>
  );
}
