import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { FaClock, FaMapMarkerAlt, FaInfoCircle, FaShoppingCart, FaCar, FaWalking } from 'react-icons/fa';
import './ExperienceDetails.css';
import { FaChevronDown, FaChevronUp } from 'react-icons/fa';
const ExperienceDetails = () => {
    const [showFullHistory, setShowFullHistory] = useState(false);
  const location = useLocation();
  const { experience } = location.state || {};
  const fullHistory = experience?.history 
  const shortHistory = fullHistory.split('.')[0] + '.';
  const displayText = showFullHistory ? fullHistory : shortHistory;
  if (!experience) {
    return <div className="not-found">No experience data found</div>;
  }

  return (
    <div className="experience-detail-container">
      <div className="detail-header">
        <div className="detail-image" style={{ backgroundImage: `url(${experience.image})` }}>
          <h1 className="detail-title">{experience.title}</h1>
        </div>
      </div>

      <div className="detail-body">
        <div className="detail-highlight">
          <p>
          {displayText}
            {fullHistory.length > shortHistory.length && (
              <button 
                onClick={() => setShowFullHistory(!showFullHistory)} 
                className="read-more-btn"
              >
                {showFullHistory ? (
                  <>
                    <FaChevronUp /> Show less
                  </>
                ) : (
                  <>
                    <FaChevronDown /> Learn more
                  </>
                )}
              </button>
            )}
          </p>
        </div>

    
      
      </div>
    </div>
  );
};

export default ExperienceDetails;
