import React from 'react';
import './ExperienceCards.css';

const ExperienceCards = () => {
  const experiences = [
    {
      title: "Hassan II Mosque",
      price: "From 80 DH",
      guidedTour: "Guided tour from MAD 400",
      distance: "Distance from airport 15kms",
      times: ["4 min", "20 min"],
      image: "https://tse3.mm.bing.net/th?id=OIP.yLVPTd3QmWQXlMBKB1lDYAHaEo&pid=Api&P=0&h=180"
    },
    {
      title: "La Corniche",
      price: "Free",
      guidedTour: "Guided tour from MAD 200",
      distance: "Distance from Hassan II Mosque",
      times: ["12 min", "1h4"],
      image: "corniche.jpg"
    },
    {
      title: "Ancienne Medina",
      price: "Free",
      guidedTour: "Guided tour from MAD 400",
      distance: "Distance from Hassan II Mosque",
      times: ["4 min", "20 min"],
      image: "medina.jpg"
    },
    {
      title: "Hobous",
      price: "Free",
      guidedTour: "Guided tour from MAD 200",
      distance: "Distance from Hassan II Mosque",
      times: ["12 min", "1h4"],
      image: "hobous.jpg"
    }
  ];

  return (
   <div className="experiences-container">
      {experiences.map((exp, index) => (
        <div key={index} className="experience-card">
          {/* Section d'image avec URL */}
          <div 
            className="card-image" 
            style={{ 
              backgroundImage: `url(${exp.image})`,
              backgroundSize: 'cover',
              backgroundPosition: 'center'
            }}
          >
            <h3 className="card-title">{exp.title}</h3>
          </div>
          
          <div className="card-details">
            <div className="price">{exp.price}</div>
            <div className="guided-tour">{exp.guidedTour}</div>
            <div className="distance">{exp.distance}</div>
            
            <div className="timings">
              <span className="time">{exp.times[0]}</span>
              <span className="time">{exp.times[1]}</span>
            </div>
            
            <div className="card-actions">
              <button className="learn-more">Learn more</button>
              <button className="buy">Buy</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ExperienceCards;
