import React from 'react';
import './ProfileHeader.css';
import NavbarHome from './NvbarHome';
import { FaMapMarkerAlt } from "react-icons/fa";
import { IoIosArrowDown } from "react-icons/io";

const ProfileHeader = () => {
  return (
    <>
      <NavbarHome/>
      <div className="profile-header-container">
        <div className="airport-header">
          {/* Ligne d'aéroport avec icône */}
          <div className="location-container">
            <FaMapMarkerAlt className="location-icon" />
            <span className="location-text">MEDVI Casablanca Airport</span>
            <IoIosArrowDown className="dropdown-icon" />
          </div>
          
          {/* Salutation */}
          <div className="greeting">HELLO CHAYMAE!</div>

          {/* Carte de membre */}
          <div className="membership-card">
            <div className="membership-id">
              <span className="membership-label">Safar Flyer</span>
              <span className="membership-number">N° 678908451</span>
            </div>
            <div className="miles-balance">10000 Award miles</div>
          </div>
        </div>

        {/* Nouveau texte ajouté */}
        <div className="airport-notice">
          <p>We noticed you are at the MEDVI Casablanca Airport.</p>
          <p>Check out activities nearby and gift options to be delivered to you. Only available to Safar Flyers members staying at RAM HUB.</p>
        </div>
      </div>
    </>
  );
};

export default ProfileHeader;
